function card(item){
  const j = pick(item);
  const sc = j.score === '' ? 'â€”' : String(j.score);
  const scColor = scoreColor(j.score);
  const chips = j.chips.length ? `<div class="chips">${j.chips.map(c=>`<span class="chip">${esc(c)}</span>`).join('')}</div>` : '';
  const bullets = j.bullets.length ? `<ul>${j.bullets.map(b=>`<li>${esc(b)}`).join('')}</ul>` : '';
  const connector = j.connector ? `<div class="connector">${esc(j.connector)}</div>` : '';
  return `
    <div class="card">
      <div class="row" style="justify-content:space-between">
        <h3 class="title">${esc(j.title)}</h3>
        <span class="score-pill" style="border-color:${scColor};color:${scColor}">Score: ${sc}/100</span>
      </div>
      <div class="meta">${esc(j.company)} â€” ${esc(j.location)}</div>
      ${chips}
      <div class="summary">${esc(j.summary)}</div>
      ${bullets}
      ${connector}
      <div class="divider"></div>
      <a class="btn" href="${esc(j.url)}" target="_blank">View & Apply</a>
    </div>
  `;
}

